// Filtrar tarefas com sendByEmail = true
const filtered = items.filter(item => {
  const task = item.json;
  return task.sendByEmail === true && 
         task.done !== true && 
         task.archived !== true &&
         Array.isArray(task.emails) &&
         task.emails.length > 0;
});

return filtered;

// Processar cada tarefa
const output = [];

for (const item of items) {
  const task = item.json;

  // Verificar horário se for envio diário
  if (task.sendFrequency === 'daily') {
    const now = new Date();
    const currentHour = now.getHours().toString().padStart(2, '0');
    const currentMinute = now.getMinutes().toString().padStart(2, '0');
    const currentTime = `${currentHour}:${currentMinute}`;
    const taskTime = task.sendTime || '09:00';

    const [taskHour, taskMinute] = taskTime.split(':').map(Number);
    const [currHour, currMinute] = currentTime.split(':').map(Number);

    const taskMinutes = taskHour * 60 + taskMinute;
    const currentMinutes = currHour * 60 + currMinute;

    // Buffer de 5 minutos
    if (Math.abs(currentMinutes - taskMinutes) > 5) {
      continue; // Pular esta tarefa
    }
  }

  // Criar um item para cada e-mail
  for (const email of task.emails) {
    output.push({
      json: {
        to: email,
        subject: task.title,
        text: task.description || 'Sem descrição',
        html: `<h2>${task.title}</h2><p>${task.description || 'Sem descrição'}</p><hr><small>ID: ${task.id}</small>`,
        taskId: task.id,
        sendFrequency: task.sendFrequency
      }
    });
  }
}

return output;
